INSERT INTO product_detail VALUES('GCTPMXW000003', 'NL ACCESSORIES','Nữ', 'Dây cổ Vàng trắng 14K đính đá Topaz NL ACESSORIES Sắc Xuân TPMXW000003 - Mùa xuân đã đến thật gần với cơn gió se lạnh mỗi sáng, vẻ rực rỡ muôn màu sắc của các loại hoa trong thiên nhiên khiến bao người xao xuyến. Cảm nhận vẻ đẹp tinh khôi điểm thêm một chút quyến rũ của thời khắc chuyển mùa, NL ACESSORIES bổ sung thêm một số sản phẩm trang sức mới trong BST Sắc xuân, mang đến những sự lựa chọn mới cho các cô gái và giúp họ tỏa sáng. Dây cổ Sắc xuân sở hữu thiết kế thời thượng và trẻ trung. Lấy cảm hứng từ vẻ đẹp đầy sự mỹ cảm và quyến rũ của những chú bướm, sản phẩm dây cổ được thiết kế với kiểu dáng cánh bướm uốn lượn mềm mại kết hợp đá trắng ECZ – Swarovski lấp lánh đính trên thân. Đồng thời, sử dụng đá quý Topaz màu xanh, tạo nên điểm nhấn nổi bật cho sản phẩm. Bên cạnh đó, dây cổ được chế tác từ chất liệu vàng trắng 14K, không chỉ mang đến vẻ đẹp thời thượng mà còn bật lên nét trẻ trung cho các cô gái. Dây cổ vàng - Món quà tinh tế và ý nghĩa. Đặc biệt hơn, chiếc dây cổ sẽ trở nên ý nghĩa hơn khi nó là món quà của bạn dành tặng cho người phụ nữ mà bạn yêu thương vào dịp cuối năm. Chắc chắn rằng, đây sẽ là món quà giúp bạn ghi dấu khoảnh khắc đáng nhớ nhất đối với cô ấy.', 5, false, true, false, false, false, false);
INSERT INTO product_detail VALUES('GCCTXMY000004', 'NL ACCESSORIES','Nữ', 'Dây cổ NL ACESSORIES Sắc xuân 18K vàng đính đá Citrine - Từ xa xưa, hoa mai luôn là loài hoa tượng trưng cho mùa xuân, là biểu tượng ngày tết cổ truyền của người Việt chúng ta. Hoa mai luôn là nguồn cảm hứng vô tận cho các nhà thiết kế để sáng tạo nên những bộ trang sức tinh tế, thể hiện sự vui tươi và vẻ đẹp truyền thống ngày Tết. Hiểu được ý nghĩa cũng như vận dụng vẻ đẹp dịu dàng của hoa mai, BST Sắc Xuân được NL ACESSORIES ra mắt với những món trang sức mang kiểu dáng của loài hoa này, đem đến phụ kiện nổi bật và tinh tế cho mùa xuân. Tô điểm sự rạng rỡ cho quý cô cùng nhẫn Sắc xuân NL ACESSORIES. Nằm trong BST mới, sản phẩm dây cổ sở hữu thiết kế hoa mai được làm nổi nhờ vào sự sang trọng của đá Citrine, các nhánh nhị hoa được chế tác tỉ mỉ và tinh tế. Bằng sự tài hoa, khéo léo của người thợ kim hoàn kết hợp cùng chất liệu vàng 18K, sợi dây cổ đính đá Citrine mang vẻ đẹp quý phái, tô điểm thêm nét rạng rỡ cho các quý cô khi đeo trên tay. BST Sắc Xuân – Tôn vinh vẻ đẹp dịu dàng của phụ nữ Việt. Mỗi loài hoa đều sở hữu riêng mình một hương sắc riêng, một vẻ đẹp riêng, không trùng lặp. Và hoa mai không biết tự khi nào đã trở thành biểu tượng ngày Tết cổ truyền của chúng ta. Lấy cảm hứng từ vẻ đẹp tinh khôi của hoa mai, NL ACESSORIES ra mắt BST Sắc Xuân với những món trang sức vàng sang trọng như vòng tay, bông tai, mề đay,… có nhiệm vụ tôn vinh nét đẹp dịu dàng của phụ nữ Việt và giúp quý cô toát lên sự sang trọng.', 5, false, true, false, false, false, false);
INSERT INTO product_detail VALUES('GCRBXMY000016', 'NL ACCESSORIES','Nữ', 'Bộ RUBY - Phượng Hoàng. Vẻ đẹp hoàng gia là vẻ đẹp đỉnh cao mà người phụ nữ luôn hướng tới. Nét đẹp ấy hội tụ sự sắc sảo, thông minh để ai cũng phải khao khát, ngưỡng mộ và sẽ càng được tôn vinh khi hòa quyện cùng nét kiêu sa, quyến rũ và sang trọng toát ra từ trang sức Ruby NL ACESSORIES.', 4, false, true, false, false, false, false);
